/*
Plan of Attack
--------------
1. Follow the line
2. Get ring
3. Push blocks
4. Knock over blocks
5. Place can on target
*/

task main()
{
	wait10Msec(200);
	print("Ready 2 Roll");
	waitpress();

	//get ring
	print("Getting Ring...");
	ring();
	waitpress();

	//push blocks
	print("Pushing Blocks...");
	push();
	waitpress();

	//knock over blocks
	print("Knocking Blocks...");
	knock();
	waitpress();

	//place can on target
	print("Placing Can...");
	can();
	waitpress();

	//follow the line
	print("Following line...");
	linefollow();

	return;
}
